mapper.select:
select pb.brand_id, pb.name,pb.brand_code,pb.brand_code_outer,pb.brand_desc,pb.create_staff,pf.file_url,pb.MANUFACTURER_ID,pb.MANUFACTURER_NAME
from prod_brand pb, prod_file pf
where pb.brand_id = pf.target_id
and pf.target_type = '4'
and pb.is_deleted = '0'

  and pb.brand_id = #{req.brandId}


  and pb.name like CONCAT(CONCAT('%',#{req.brandName}),'%')


  and pb.brand_code like CONCAT(CONCAT('%', #{req.brandCode}), '%')


  and pb.brand_code_outer = #{req.brandCodeOuter}


  and pb.create_staff = #{req.createStaff}


  and pb.MANUFACTURER_ID = #{req.manufacturerId}


  and pb.MANUFACTURER_NAME like CONCAT(CONCAT('%', #{req.manufacturerName}), '%')

ORDER BY pb.CREATE_DATE DESC

select q.goodsId,q.goodsName,q.categoryId,q.retailPrice,q.wholeSalePrice,q.orderAmount,q.supplierId,q.imageUrl from ( select DISTINCT p.GOODS_ID as goodsId,p.GOODS_NAME as goodsName,p.GOODS_CAT_ID as categoryId, p.MKTPRICE as retailPrice,p.DELIVERY_PRICE as wholeSalePrice,p.BUY_COUNT as orderAmount, p.SUPPLIER_ID as supplierId,t.FILE_URL as imageUrl,p.CREATE_DATE from (SELECT pg.GOODS_ID ,pg.GOODS_NAME ,pg.GOODS_CAT_ID , pg.MKTPRICE ,pgpr.DELIVERY_PRICE ,pg.BUY_COUNT , pg.SUPPLIER_ID ,pb.CREATE_DATE,pgrr.REGION_ID FROM PROD_GOODS_PRODUCT_REL pgpr ,PROD_GOODS pg ,PROD_BRAND pb,PROD_GOODS_REGION_REL pgrr WHERE pgpr.GOODS_ID=pg.GOODS_ID AND pb.BRAND_ID = pg.BRAND_ID AND pg.GOODS_ID = pgrr.GOODS_ID AND pg.MARKET_ENABLE='1' AND pg.TARGET_TYPE='1'
and pb.brand_id = #{req.brandId}
and (pgrr.REGION_ID = #{req.regionId} or pgrr.REGION_ID=pgrr.LAN_ID)
and pgrr.LAN_ID = #{req.lanId}
and pgpr.PRODUCT_ID in
#{item}
) p LEFT JOIN (select pc.TARGET_ID,pc.FILE_URL from prod_file pc where pc.SUB_TYPE='6' and pc.FILE_TYPE='1' and pc.TARGET_TYPE='1') t on t.TARGET_ID = p.GOODS_ID UNION ALL select DISTINCT p.GOODS_ID as goodsId,p.GOODS_NAME as goodsName,p.GOODS_CAT_ID as categoryId, p.MKTPRICE as retailPrice,p.DELIVERY_PRICE as wholeSalePrice,p.BUY_COUNT as orderAmount, p.SUPPLIER_ID as supplierId,t.FILE_URL as imageUrl,p.CREATE_DATE from (SELECT pg.GOODS_ID ,pg.GOODS_NAME ,pg.GOODS_CAT_ID , pg.MKTPRICE ,pgpr.DELIVERY_PRICE ,pg.BUY_COUNT , pg.SUPPLIER_ID ,pb.CREATE_DATE FROM PROD_GOODS_PRODUCT_REL pgpr ,PROD_GOODS pg ,PROD_BRAND pb,prod_goods_target_rel pgtr WHERE pgpr.GOODS_ID=pg.GOODS_ID AND pb.BRAND_ID = pg.BRAND_ID AND pg.GOODS_ID = pgtr.GOODS_ID AND pg.MARKET_ENABLE='1' AND pg.TARGET_TYPE='2'
and pb.brand_id = #{req.brandId}
and pgpr.PRODUCT_ID in
#{item}
and pgtr.TARGET_ID=#{req.supplierId}
) p LEFT JOIN (select pc.TARGET_ID,pc.FILE_URL from prod_file pc where pc.SUB_TYPE='6' and pc.FILE_TYPE='1' and pc.TARGET_TYPE='1') t on t.TARGET_ID = p.GOODS_ID ) q ORDER BY q.CREATE_DATE DESC
