mapper.select:
select MIN(p.COST) from prod_product p
inner join prod_goods_product_rel pr on p.PRODUCT_ID=pr.PRODUCT_ID
where pr.GOODS_ID = #{goodsId}

select DISTINCT p.GOODS_ID as goodsId,p.GOODS_NAME as goodsName,p.GOODS_CAT_ID as categoryId,p.CAT_PATH as parCategoryId, p.MKTPRICE as retailPrice,p.DELIVERY_PRICE as wholeSalePrice,p.BUY_COUNT as orderAmount, p.SUPPLIER_ID as supplierId, t.FILE_URL as imageUrl from ( SELECT pg.GOODS_ID ,pg.GOODS_NAME ,pg.GOODS_CAT_ID ,pc.CAT_PATH , pg.MKTPRICE ,pgpr.DELIVERY_PRICE ,pg.BUY_COUNT , pg.SUPPLIER_ID FROM PROD_GOODS_PRODUCT_REL pgpr ,PROD_GOODS pg , PROD_CAT pc, prod_goods_region_rel pgrr WHERE pgpr.GOODS_ID=pg.GOODS_ID AND pg.GOODS_CAT_ID=pc.CAT_ID AND pg.GOODS_ID = pgrr.GOODS_ID AND pg.TARGET_TYPE='1' AND pgpr.PRODUCT_ID in
#{item}
and pgrr.LAN_ID = #{req.lanId}
and (pgrr.REGION_ID = #{req.regionId} or pgrr.REGION_ID = pgrr.LAN_ID)
) p LEFT JOIN (select pc.TARGET_ID,pc.FILE_URL from prod_file pc where pc.SUB_TYPE='6' and pc.FILE_TYPE='1' and pc.TARGET_TYPE='1') t on t.TARGET_ID = p.GOODS_ID UNION ALL select DISTINCT p.GOODS_ID as goodsId,p.GOODS_NAME as goodsName,p.GOODS_CAT_ID as categoryId,p.CAT_PATH as parCategoryId, p.MKTPRICE as retailPrice,p.DELIVERY_PRICE as wholeSalePrice,p.BUY_COUNT as orderAmount, p.SUPPLIER_ID as supplierId, t.FILE_URL as imageUrl from ( SELECT pg.GOODS_ID ,pg.GOODS_NAME ,pg.GOODS_CAT_ID ,pc.CAT_PATH , pg.MKTPRICE ,pgpr.DELIVERY_PRICE ,pg.BUY_COUNT , pg.SUPPLIER_ID,pg.TARGET_TYPE FROM PROD_GOODS_PRODUCT_REL pgpr ,PROD_GOODS pg , PROD_CAT pc, prod_goods_target_rel pgtr WHERE pgpr.GOODS_ID=pg.GOODS_ID AND pg.GOODS_CAT_ID=pc.CAT_ID AND pg.GOODS_ID = pgtr.GOODS_ID AND pg.TARGET_TYPE='2' AND pgpr.PRODUCT_ID in
#{item}
and pgtr.TARGET_ID = #{req.merchantId}
) p LEFT JOIN (select pc.TARGET_ID,pc.FILE_URL from prod_file pc where pc.SUB_TYPE='6' and pc.FILE_TYPE='1' and pc.TARGET_TYPE='1') t on t.TARGET_ID = p.GOODS_ID
