and t.product_id in
#{item}
and pb.brand_id in
#{item}

select t.*,ppb.product_name,ppb.unit_type_name,pb.name as brand_name,ppb.unit_type from prod_product t left join prod_product_base ppb on t.product_base_id = ppb.product_base_id left join prod_brand pb on pb.brand_id = ppb.brand_id where 1 = 1 and t.is_deleted = '0' and ppb.is_deleted = '0' and pb.is_deleted = '0'
and upper(ppb.PRODUCT_NAME) like CONCAT(CONCAT('%',upper(#{req.productName})),'%')
and upper(pb.name) like CONCAT(CONCAT('%',upper(#{req.brandName})),'%')
and pb.BRAND_ID =#{req.brandId}
and upper(ppb.unit_Type) like CONCAT(CONCAT('%',upper(#{req.unitType})),'%')
and ppb.type_id =#{req.typeId}

