mapper.select:
		SELECT  
        FROM t_content_base t
		WHERE content_id=#{contentId}

SELECT distinct t.CONTENT_ID, t.TITLE, t.DESP, t.CATALOG_ID, t.EFF_DATE, t.EXP_DATE, t.TYPE, t.STATUS, t.OPR_ID, t.UPD_DATE, t.MAXPICNUM, t.PLAYINTERVAL from t_content_base t
and t.catalog_id=#{pageReq.catalogId}
and t.type in
#{item}
and t.status in
#{item}
and t.content_id in(select distinct content_id from t_content_tag where tag_id in
#{item} )
and t.opr_id =#{pageReq.oprid}
and t.upd_date >= #{pageReq.beginTime}
and t.upd_date <= #{pageReq.endTime}
and (t.title like CONCAT(CONCAT('%', #{pageReq.fieldVal}), '%') or d.name like CONCAT(CONCAT('%', #{pageReq.fieldVal}), '%'))
and t.title like CONCAT(CONCAT('%', #{pageReq.title}), '%')
and now() BETWEEN t.EFF_DATE and t.EXP_DATE
order by t.upd_date desc

SELECT from t_content_base t
now() BETWEEN t.EFF_DATE and t.EXP_DATE
and t.content_id in (select DISTINCT(contentid) from t_content_publish p where p.area like concat(concat('%',#{pageReq.area}),'%'))
and t.status = #{pageReq.status}
and t.type = #{pageReq.type}
order by t.upd_date desc

SELECT  
from t_content_base t


        and t.catalog_id=#{catalogId}


        and t.type =#{type}


        and t.status =#{status}


        and t.opr_id =#{oprid}

SELECT  
from t_content_base t


    exp_date < sysdate + #{days}
    and t.status <>  ${@com.iwhalecloud.retail.oms.consts.OmsConst$ContentStatusEnum@OFF_SHELVE.getCode()}
    and t.status <>  ${@com.iwhalecloud.retail.oms.consts.OmsConst$ContentStatusEnum@HAVE_DELETE.getCode()}

    -- 在有效期间内的内容才给与显示
    --and current_date BETWEEN t.EFF_DATE and t.EXP_DATE

mapper.update:
UPDATE t_content_base t

    t.status = ${@com.iwhalecloud.retail.oms.consts.OmsConst$ContentStatusEnum@OFF_SHELVE.getCode()}



    exp_date < sysdate
    and t.status <>  ${@com.iwhalecloud.retail.oms.consts.OmsConst$ContentStatusEnum@OFF_SHELVE.getCode()}
    and t.status <>  ${@com.iwhalecloud.retail.oms.consts.OmsConst$ContentStatusEnum@HAVE_DELETE.getCode()}

    -- 在有效期间内的内容才给与显示
    --and current_date BETWEEN t.EFF_DATE and t.EXP_DATE

mapper.select:
SELECT distinct t.CONTENT_ID, t.TITLE, t.DESP, t.CATALOG_ID, t.EFF_DATE, t.EXP_DATE, t.TYPE, t.STATUS, t.OPR_ID, t.UPD_DATE, t.MAXPICNUM, t.PLAYINTERVAL from t_content_base t, (
select t.contentid from t_content_orderpic t where t.objtype = #{pageReq.objType}
select t.contentid from t_content_pic t where t.objtype = #{pageReq.objType}
select t.contentid from t_content_picset t where t.objtype = #{pageReq.objType}
select t.contentid from t_content_video t where t.objtype = #{pageReq.objType}
select t.contentid from t_content_vediolv2 v, t_content_video t where v.objtype = #{pageReq.objType} and t.matid = v.upmatid
) p, t_content_publish cp
and t.content_id=p.contentid and t.content_id = cp.contentId
and now() BETWEEN t.EFF_DATE and t.EXP_DATE
and cp.area like CONCAT(CONCAT('%', #{pageReq.area}), '%')
and t.title like CONCAT(CONCAT('%', #{pageReq.title}), '%')
and t.status in
#{item}
and t.catalog_id=#{pageReq.catalogId}
order by t.upd_date desc
