mapper.select:
SELECT e.EVENT_EXTEND AS GOODS_ID,e.EVENT_EXTEND_EXTRA1 AS GOODS_NAME, count(1) AS GOODS_EVENT_COUNT
FROM EVENT e LEFT JOIN CLOUD_DEVICE c ON e.DEVICE_NUMBER = c.NUM
WHERE e.IS_DELETED = 0

    AND c.ADSCRIPTION_CITY = #{countRankReq.adscriptionCity}


    AND e.EVENT_CODE = #{countRankReq.eventCode}


    AND e.GMT_CREATE >= #{countRankReq.beginTime}


    AND #{countRankReq.endTime} >= e.GMT_CREATE

GROUP BY EVENT_EXTEND,EVENT_EXTEND_EXTRA1
ORDER BY GOODS_EVENT_COUNT DESC

SELECT e.EVENT_EXTEND AS GOODS_ID,e.EVENT_EXTEND_EXTRA1 AS GOODS_NAME,
CONCAT(FORMAT(AVG(CAST(EVENT_EXTEND_EXTRA2 AS SIGNED INTEGER))*20,1),'%') AS GOODS_EVENT_COUNT
FROM EVENT e LEFT JOIN CLOUD_DEVICE c ON e.DEVICE_NUMBER = c.NUM
WHERE e.IS_DELETED = 0

    AND c.ADSCRIPTION_CITY = #{evaluateRankReq.adscriptionCity}


    AND e.EVENT_CODE = #{evaluateRankReq.eventCode}


    AND e.GMT_CREATE >= #{evaluateRankReq.beginTime}


    AND #{evaluateRankReq.endTime} >= e.GMT_CREATE

GROUP BY EVENT_EXTEND,EVENT_EXTEND_EXTRA1
ORDER BY GOODS_EVENT_COUNT DESC
