mapper.delete:
		delete from cloud_shelf_detail


                 id=#{id}


                 and cloud_shelf_number=#{cloudShelfNumber}


                 and shelf_category_id=#{shelfCategoryId}

mapper.update:
update cloud_shelf_detail




                when CLOUD_SHELF_NUMBER = #{item.cloudShelfNumber} then #{item.shelfCategoryId}






                when CLOUD_SHELF_NUMBER = #{item.cloudShelfNumber} then #{item.operatingPositionId}




where  CLOUD_SHELF_NUMBER = #{item.cloudShelfNumber}

mapper.select:
		select * from cloud_shelf_detail


                and cloud_shelf_number=#{cloudShelfNumber}


                and shelf_category_id=#{shelfCategoryId}


        order by OPERATING_POSITION_SEQUENCE desc
