mapper.select:
select  from ACCOUNT_BALANCE t inner join ACCOUNT_BALANCE_TYPE t1
on t.BALANCE_TYPE_ID=t1.BALANCE_TYPE_ID inner join ACCOUNT_BALANCE_RULE t2 on t1.BALANCE_TYPE_ID=t2.BALANCE_TYPE_ID
where 1=1

    and t.CUST_ID = #{req.custId}


    and t1.ACT_ID = #{req.actId}



    and t2.RULE_TYPE = #{req.ruleType}


    and t2.OBJ_TYPE = #{req.objType}


    and t2.OBJ_ID = #{req.objId}

SELECT SUM(A.AMOUNT) AS TOTAL_AMOUNT,SUM(A.UNEFFECTIVE_AMOUNT) AS TOTAL_UN_EFF_AMOUNT, SUM(A.INVALID_AMOUNT) TOTAL_INVAILD_AMOUNT FROM ACCOUNT_BALANCE A INNER JOIN ACCOUNT B ON A.ACCT_ID=B.ACCT_ID WHERE B.STATUS_CD='1000' AND B.EFF_DATE<=SYSDATE() AND B.EXP_DATE>=SYSDATE() AND A.STATUS_CD='1000' AND A.EFF_DATE<=SYSDATE() AND A.EXP_DATE>=SYSDATE()
and B.ACCT_TYPE = #{req.acctType}
and B.ACCT_ID = #{req.acctId}

SELECT count(A.ACCOUNT_BALANCE_ID)  FROM ACCOUNT_BALANCE A
INNER JOIN ACCOUNT B ON A.ACCT_ID=B.ACCT_ID WHERE
  A.STATUS_CD='1000' AND A.EFF_DATE<=SYSDATE() AND A.EXP_DATE>=SYSDATE()

    and B.ACCT_TYPE = #{req.acctType}


    and B.ACCT_ID = #{req.acctId}

select a.ACCT_ID,a.ACCOUNT_BALANCE_ID,a.AMOUNT,a.UNEFFECTIVE_AMOUNT, a.INVALID_AMOUNT,a.CYCLE_LOWER,a.CYCLE_UPPER,a.EFF_DATE,a.EXP_DATE,a.STATUS_CD,a.CYCLE_TYPE, a.AMOUNT as total_Amount,d.OBJ_ID as supplier_Id ,b.CUST_ID,c.BALANCE_TYPE_ID,c.BALANCE_TYPE_NAME from account_balance a inner join account b on a.ACCT_ID=b.ACCT_ID inner join ACCOUNT_BALANCE_TYPE c on a.BALANCE_TYPE_ID=c.BALANCE_TYPE_ID inner join ACCOUNT_BALANCE_RULE d on c.BALANCE_TYPE_ID=d.BALANCE_TYPE_ID where b.STATUS_CD='1000' AND b.EFF_DATE<=SYSDATE() AND b.EXP_DATE>=SYSDATE()
and B.CUST_ID = #{req.custId}
and B.ACCT_TYPE = #{req.acctType}
and B.ACCT_ID = #{req.acctId}
and d.obj_id = #{req.supplierId}
and DATE_FORMAT(a.EFF_DATE, '%Y-%m-%d')>= DATE_FORMAT(#{req.effDateStart}, '%Y-%m-%d')
and DATE_FORMAT(a.EFF_DATE, '%Y-%m-%d')<= DATE_FORMAT(#{req.effDateEnd}, '%Y-%m-%d')
