SELECT d.MERCHANT_ID AS supplier_Id,a.AMOUNT,a.BALANCE,d.ORDER_ID,d.PRODUCT_ID,a.OPER_DATE,a.PAYOUT_DESC FROM ACCOUNT_BALANCE_PAYOUT A INNER JOIN ACCOUNT_BALANCE b ON a.ACCT_BALANCE_ID = b.ACCOUNT_BALANCE_ID INNER JOIN ACCOUNT_BALANCE_ITEM_PAYED c ON a.OPER_PAYOUT_ID = c.OPER_PAYOUT_ID INNER JOIN ACCOUNT_ITEM d ON c.ACCT_ITEM_ID = d.ACCT_ITEM_ID INNER JOIN account e on d.acct_id=e.ACCT_ID where A.STATUS_CD='1000' and e.STATUS_CD='1000' AND e.EFF_DATE<=SYSDATE() AND e.EXP_DATE>=SYSDATE()
and d.MERCHANT_ID in
#{item}
and d.PRODUCT_ID in
#{item}
and e.cust_id=#{req.custId}
and DATE_FORMAT(a.OPER_DATE, '%Y-%m-%d')<= DATE_FORMAT(#{req.operDateEnd}, '%Y-%m-%d')
and DATE_FORMAT(a.OPER_DATE, '%Y-%m-%d')>= DATE_FORMAT(#{req.operDateStart}, '%Y-%m-%d')
