mapper.select:
select

from act_marketing_activity t where 1=1
and t.IS_DELETED = '0'

    and lower(t.NAME) like lower(CONCAT(CONCAT('%', #{req.activityName }), '%'))


    and lower(t.CODE) = #{req.activityCode }


    and lower(t.ACTIVITY_TYPE) = #{req.activityType }


    and lower(t.STATUS) =  #{req.activityStatus}


    and lower(t.START_TIME) >= #{req.startTimeS}


    and lower(t.START_TIME) <= #{req.startTimeE}


    and lower(t.END_TIME) >= #{req.endTimeS}


    and lower(t.END_TIME) <= #{req.endTimeE}


    and lower(t.DELIVER_START_TIME) >= #{req.deliverStartTimeS}


    and lower(t.DELIVER_START_TIME) <= #{req.deliverStartE}


    and lower(t.DELIVER_END_TIME) >= #{req.deliverEndTimeS}


    and lower(t.DELIVER_END_TIME) <= #{req.deliverEndTimeE}


    and lower(t.INITIATOR) = #{req.activityInitiator}


    and t.creator = #{req.creator}


order by t.GMT_CREATE DESC

select t.id,t.name,t.brief,t.description,t.pay_type,t.pre_start_time,t.pre_end_time,t.tail_pay_start_time,t.tail_pay_end_time ,b.pre_price from act_marketing_activity t,act_activity_product b where 1=1 AND t.id = b.MARKETING_ACTIVITY_ID
AND t.id = #{req.marketingActivityId}
AND b.product_id = #{req.productId}

select
from act_marketing_activity t where 1=1 and t.IS_DELETED = '0'
AND t.ID = #{id}

SELECT ama.id,ama.name,ama.start_time,(CASE when (ama.status = '20') THEN ama.end_time ELSE ama.GMT_MODIFIED END) as end_time FROM act_marketing_activity ama WHERE 1=1 and ama.is_deleted = '0' AND (ama.status = '20' OR ama.status = '40') and end_time < now()
and ama.name like concat('%',#{activityName},'%')
and ama.activity_type = #{activityType}
