SELECT a.MKT_RES_INST_NBR AS mktResInstNbr, a.PRODUCT_NAME AS productName, a.UNIT_TYPE AS unitType, b.TYPE_NAME AS typeName, a.BRAND_NAME AS brandName, c.ATTR_VALUE1 AS attrValue1, c.ATTR_VALUE2 AS attrValue2, c.ATTR_VALUE3 AS attrValue3, a.STATUS_CD AS statusCd, a.MKT_RES_INST_TYPE AS mktResInstType, a.SOURCE_TYPE AS sourceType, a.PRODUCT_CODE AS productCode, a.ORDER_ID AS orderId, a.CREATE_TIME AS createTime, a.SUPPLIER_NAME AS supplierName, a.SUPPLIER_CODE AS supplierCode, a.PARTNER_NAME AS partnerName, a.PARTNER_CODE AS partnerCode, s1.region_name AS cityId, so.org_name AS countyOrgName, a.BUSINESS_ENTITY_NAME AS businessEntityName, a.RECEIVE_TIME AS receiveTime, a.OUT_TIME AS outTime, a.STOCK_AGE AS stockAge, (CASE WHEN a.stock_age >=30 AND a.stock_age <60 THEN '1' ELSE '0' END) as day30, (CASE WHEN a.stock_age >=60 AND a.stock_age <90 THEN '1' ELSE '0' END) as day60, (CASE WHEN a.stock_age >=90 THEN '1' ELSE '0' END) as day90, a.DEST_MERCHANT_ID AS destMerchantId, s3.region_name AS destCityId, so.org_name AS destCountyOrgName, a.CRM_STATUS AS crmStatus, a.SELF_REG_STATUS AS selfRegStatus FROM rpt_res_inst_detail_admin a LEFT JOIN prod_type b ON a.product_code = b.type_id LEFT JOIN prod_product c ON a.product_id = c.product_id LEFT JOIN prod_product_base d ON c.product_base_id = d.product_base_id LEFT JOIN sys_common_region s1 ON a.city_id = s1.region_id LEFT JOIN sys_common_region s3 ON a.DEST_CITY_ID = s3.region_id LEFT JOIN par_merchant par ON par.merchant_id = a.DEST_MERCHANT_ID LEFT JOIN sys_organization so ON par.par_crm_org_id = so.org_id WHERE 1=1
and str_to_date(a.CREATE_TIME,'%Y-%m-%d') >= str_to_date(#{req.xdCreateTimeStart}, '%Y-%m-%d')
and str_to_date(a.CREATE_TIME,'%Y-%m-%d') <= str_to_date(#{req.xdCreateTimeEnd}, '%Y-%m-%d')
and s1.region_id in
#{item}
and
INSTR( so.path_code, #{item})
and a.STATUS_CD = #{req.statusCd}
and str_to_date(a.RECEIVE_TIME,'%Y-%m-%d') >= str_to_date(#{req.createTimeStart}, '%Y-%m-%d')
and str_to_date(a.RECEIVE_TIME,'%Y-%m-%d') <= str_to_date(#{req.createTimeEnd}, '%Y-%m-%d')
and a.MKT_RES_INST_TYPE = #{req.mktResInstType}
and a.SOURCE_TYPE = #{req.sourceType}
and a.SELF_REG_STATUS = #{req.selfRegStatus}
and str_to_date(a.OUT_TIME,'%Y-%m-%d') >= str_to_date(#{req.outTimeStart}, '%Y-%m-%d')
and str_to_date(a.OUT_TIME,'%Y-%m-%d') <= str_to_date(#{req.outTimeEnd}, '%Y-%m-%d')
and a.product_type = #{req.productType}
and a.BRAND_ID = #{req.brandName}
and a.CRM_STATUS = #{req.crmStatus}
and a.MKT_RES_INST_NBR = #{req.mktResInstNbr}
and a.UNIT_TYPE = #{req.unitType}
and a.PRODUCT_NAME = #{req.productName}
and a.ORDER_ID = #{req.orderId}
and a.PRODUCT_CODE = #{req.productCode}
and s3.region_id in
#{item}
and a.SUPPLIER_NAME like CONCAT(CONCAT('%', #{req.supplierName}),'%')
and a.SUPPLIER_CODE = #{req.supplierCode}
and
INSTR( so.path_code, #{item})
and a.PARTNER_NAME like CONCAT(CONCAT('%', #{req.partnerName}),'%')
and a.PARTNER_CODE = #{req.partnerCode}
and a.BUSINESS_ENTITY_NAME like CONCAT(CONCAT('%', #{req.businessEntityName}),'%')
and par.MERCHANT_NAME like CONCAT(CONCAT('%', #{req.destMerchantName}),'%')

