mapper.select:
SELECT b.product_name AS productName, d.UNIT_TYPE AS unitType, e.type_name AS typeName, b.brand_name AS brandName, c.attr_value1 AS attrValue1, c.attr_value2 AS attrValue2, c.attr_value3 AS attrValue3, a.order_id AS orderId, a.STATUS AS STATUS, a.source_from AS sourceFrom, a.ORDER_CAT orderCat, a.ORDER_TYPE AS orderType, a.PAYMENT_TYPE AS paymentType, b.sn AS sn, b.num AS num, b.price AS price, (b.num*b.price) AS totalMoney, a.CREATE_TIME AS createTime, a.PAY_TIME AS payTime, a.RECEIVE_TIME AS receiveTime, GROUP_CONCAT(l.name) AS activeName, k.PROMOTION_TYPE AS couponType, k.DISCOUNT AS couponMoney, (b.num*k.DISCOUNT) AS totalCouponMoney, a.SUPPLIER_NAME AS supplierName, a.SUPPLIER_ID AS supplierCode, a.USER_NAME AS merchantName, par.MERCHANT_CODE AS merchantCode, par.BUSINESS_ENTITY_NAME AS businessEntityName, par.LAN_ID AS lanId, so.org_name as orgName, a.create_time, b.item_id AS itemId FROM ord_order a LEFT JOIN par_merchant par ON par.merchant_code = a.user_id LEFT JOIN sys_organization so ON par.par_crm_org_id = so.org_id ,ord_order_items b LEFT JOIN prod_product c ON b.PRODUCT_ID=c.PRODUCT_ID LEFT JOIN prod_product_base d ON c.product_base_id= d.product_base_id LEFT JOIN prod_type e ON d.type_id = e.type_id LEFT OUTER JOIN ORD_PROMOTION k ON b.item_id = k.order_item_id LEFT OUTER JOIN act_marketing_activity l ON k.MKT_ACT_ID = l.id
WHERE a.order_id = b.order_id
and a.CREATE_TIME >= str_to_date(#{req.createTimeStart}, '%Y-%m-%d')
and a.CREATE_TIME <= str_to_date(#{req.createTimeEnd}, '%Y-%m-%d')
and a.RECEIVE_TIME >= str_to_date(#{req.outTimeStart}, '%Y-%m-%d')
and a.RECEIVE_TIME <= str_to_date(#{req.outTimeEnd}, '%Y-%m-%d')
and a.status=#{req.status}
and a.order_id = #{req.orderId}
and par.LAN_ID in
#{item}
and INSTR( so.path_code, #{req.orgName})
and a.ORDER_CAT =#{req.orderCat}
and a.SUPPLIER_NAME like CONCAT(CONCAT('%',#{req.suplierName}),'%')
and a.SUPPLIER_CODE = #{req.suplierCode}
and a.USER_NAME like CONCAT(CONCAT('%',#{req.merchantName}),'%')
and par.MERCHANT_CODE = #{req.merchantCode}
and par.BUSINESS_ENTITY_NAME like CONCAT(CONCAT('%',#{req.businessEntityName}),'%')
and a.payment_type=#{req.paymentType}
and a.pay_type=#{req.payType}
and d.BRAND_ID=#{req.brandName}
and b.product_name like CONCAT(CONCAT('%',#{req.productName}),'%')
and d.unit_type=#{req.unitType}
and d.PRODUCT_BASE_ID=#{req.productBaseId}
and b.SN = #{req.sn}
and a.order_type = #{req.orderType}
and l.name LIKE CONCAT(CONCAT('%',#{req.activeName}),'%')
and d.type_id = #{req.typeName}
GROUP BY b.item_id ORDER BY a.create_time DESC
