mapper.select:
select supplier_id, supplier_code, supplier_name, sum(SELL_NUM) SELL_NUM, sum(SELL_AMOUNT) SELL_AMOUNT, sum(PURCHASE_AMOUNT) PURCHASE_AMOUNT, sum(PURCHASE_NUM) PURCHASE_NUM, sum(MANUAL_NUM) MANUAL_NUM, sum(TRANS_IN_NUM) TRANS_IN_NUM, sum(TRANS_OUT_NUM) TRANS_OUT_NUM, sum(RETURN_NUM) RETURN_NUM, sum(STOCK_NUM) STOCK_NUM, sum(STOCK_AMOUNT) STOCK_AMOUNT, (sum(TRANS_IN_NUM)+sum(MANUAL_NUM)+sum(PURCHASE_NUM)) TOTAL_IN_NUM , (sum(TRANS_OUT_NUM)+sum(RETURN_NUM)+sum(SELL_NUM)) TOTAL_OUT_NUM, -- 7天平均日销量 IFNULL( (select ROUND(sum(b.SELL_NUM)/7, 0) a from rpt_supplier_operating_day b where b.CREATE_DATE >=DATE_SUB(CURDATE(), INTERVAL 7 DAY) GROUP BY b.SUPPLIER_ID ), 0) AVERAGE_DAILY_SALES
from rpt_supplier_operating_day t where 1=1
and lower(t.SUPPLIER_NAME) like lower(CONCAT(CONCAT('%', #{req.supplierName }), '%'))
and lower(t.SUPPLIER_CODE) like lower(CONCAT(CONCAT('%', #{req.supplierCode }), '%'))
and t.SUPPLIER_ID = #{req.supplierId}
and t.CITY_ID = #{req.cityId}
and t.COUNTY_ID = #{req.countyId}
and t.PRODUCT_BASE_ID = #{req.productBaseId}
and t.PRODUCT_ID = #{req.productId}
and t.BRAND_ID = #{req.brandId}
and t.item_date >= #{req.startDate}
and t.item_date <= #{req.endDate} -- and t.item_date <= str_to_date(#{req.outTimeEnd}, '%Y-%m-%d')
group by t.SUPPLIER_ID

mapper.sql:
select * from ( select * from ( select d.*,d.sumStockDay/d.daySale sevenDayLv , CASE WHEN (d.sumStockDay/d.daySale)>= 10 THEN 1 WHEN (d.sumStockDay/d.daySale) >= 5 && (d.sumStockDay/d.daySale)<10 THEN 2 ELSE 3 END AS redStatus from ( select a.*, (select ROUND(sum(b.SELL_NUM)/7 ,2) from rpt_supplier_operating_day b where a.supplierName=b.supplier_name and a.supplierCode=b.SUPPLIER_CODE and a.PRODUCT_BASE_ID=b.PRODUCT_BASE_ID and a.BRAND_ID=b.BRAND_ID and a.priceLevel=b.PRICE_LEVEL and b.CREATE_DATE>=DATE_SUB(CURDATE(), INTERVAL 7 DAY) ) daySale, (select sum(b.STOCK_NUM) from rpt_supplier_operating_day b where a.supplierName=b.supplier_name and a.supplierCode=b.SUPPLIER_CODE and a.PRODUCT_BASE_ID=b.PRODUCT_BASE_ID and a.BRAND_ID=b.BRAND_ID and a.priceLevel=b.PRICE_LEVEL and to_days(b.CREATE_DATE) = to_days(now()) ) sumStockDay, (select sum(b.STOCK_AMOUNT) from rpt_supplier_operating_day b where a.supplierName=b.supplier_name and a.supplierCode=b.SUPPLIER_CODE and a.PRODUCT_BASE_ID=b.PRODUCT_BASE_ID and a.BRAND_ID=b.BRAND_ID and a.priceLevel=b.PRICE_LEVEL and to_days(b.CREATE_DATE) = to_days(now()) ) sumStockAmoutDay from ( select PRODUCT_BASE_ID,BRAND_ID, COUNTY_ID,city_id, supplier_name supplierName ,SUPPLIER_CODE supplierCode, PRODUCT_BASE_NAME productName, BRAND_NAME brandName,PRICE_LEVEL priceLevel, (sum(TRANS_IN_NUM)+sum(MANUAL_NUM)+sum(PURCHASE_NUM)) totalInnum , (sum(TRANS_OUT_NUM)+sum(RETURN_NUM)+sum(SELL_NUM)) totalOutNum, sum(PURCHASE_NUM) sumPurchase, sum(PURCHASE_AMOUNT) amoutPurcchase,sum(MANUAL_NUM) sumMANUAL, sum(TRANS_IN_NUM) sumTransIn,sum(SELL_NUM) sumSellNum, sum(SELL_AMOUNT) sumSellAmout,sum(TRANS_OUT_NUM) sumTransOut,sum(RETURN_NUM) sumReturn,type_id typeId from rpt_supplier_operating_day where 1=1
and item_date >= str_to_date(#{req.createTimeStart}, '%Y-%m-%d')
and item_date <= str_to_date(#{req.createTimeEnd}, '%Y-%m-%d')
and item_date >= str_to_date(#{req.outTimeStart}, '%Y-%m-%d')
and item_date <= str_to_date(#{req.outTimeEnd}, '%Y-%m-%d')
and type_id = #{req.typeId}
group by supplier_name, SUPPLIER_CODE,PRODUCT_BASE_ID ,BRAND_ID,PRICE_LEVEL ) a ) d where 1=1
and d.brand_id = #{req.brand}
and d.product_base_id = #{req.productType}
and d.suppliername like CONCAT(CONCAT('%',#{req.merchantName}),'%')
and d.suppliercode like CONCAT(CONCAT('%',#{req.merchantCode}),'%')
and d.pricelevel=#{req.gear}
and d.city_id =#{req.lanId}
and d.COUNTY_ID=#{req.city}
) x where 1=1
and redStatus=#{req.warningStatus}
limit 60000 ) z
