mapper.select:
SELECT ci.coupon_inst_id, ci.COUPON_INST_NBR AS couponNo, mrc.MKT_RES_NAME AS couponName, mrc.COUPON_TYPE AS couponType, mrc.partner_id AS partnerId, mrc.SHOW_AMOUNT AS amount, CASE WHEN ci.STATUS_CD = '1200' THEN '未使用' WHEN mrc.STATUS_CD = '1100' THEN '已过期' WHEN mrc.STATUS_CD = '1400' THEN '未生效' ELSE '' END AS couponStatus, ci.EFF_DATE AS startTime, ci.EXP_DATE AS expiryDate FROM COUPON_INST ci, MKT_RES_COUPON mrc WHERE ci.MKT_RES_ID = mrc.MKT_RES_ID
and ci.STATUS_CD = #{req.couponStatus}
and ci.STATUS_CD in (1100,1200,1400)
and ci.COUPON_INST_NBR = #{req.couponNo}
and mrc.COUPON_TYPE = #{req.couponName}
and ci.CUST_ACCT_ID = #{req.businessId}
and mrc.COUPON_TYPE = #{req.couponType}
and ci.EXP_DATE >= #{req.startDate}
and ci.EXP_DATE <= #{req.endDate}
order by ci.STATUS_CD desc,ci.EXP_DATE desc

SELECT ci.coupon_inst_id, ci.COUPON_INST_NBR as couponNo, mrc.MKT_RES_NAME AS couponName, mrc.COUPON_TYPE AS couponType, mrc.partner_id as partnerId, mrc.SHOW_AMOUNT AS amount, ci.EFF_DATE, ci.EXP_DATE as expiryDate, ciur.USE_DATE, ciur.USE_ORDER_ID as orderNo FROM COUPON_INST ci, MKT_RES_COUPON mrc, COUPON_INST_USE_REC ciur WHERE ci.MKT_RES_ID = mrc.mkt_res_id AND ciur.COUPON_INST_ID = ci.coupon_inst_id and ci.STATUS_CD = '1300'
and ci.CUST_ACCT_ID = #{req.businessId}
and mrc.MKT_RES_NAME = #{req.couponName}
and ciur.USE_ORDER_ID = #{req.orderNo}
and ci.COUPON_INST_NBR = #{req.couponNo}
and mrc.COUPON_TYPE = #{req.couponType}

SELECT ci.coupon_inst_id, ci.COUPON_INST_NBR as couponNo, mrc.MKT_RES_NAME AS couponName, mrc.COUPON_TYPE AS couponType, mrc.partner_id as partnerId, mrc.SHOW_AMOUNT AS amount, ci.EFF_DATE, ci.EXP_DATE as expiryDate, ciur.USE_DATE, ciur.USE_ORDER_ID as orderNo FROM COUPON_INST ci, MKT_RES_COUPON mrc, COUPON_INST_USE_REC ciur WHERE ci.MKT_RES_ID = mrc.mkt_res_id AND ciur.COUPON_INST_ID = ci.coupon_inst_id and ci.STATUS_CD = '1300'
and ci.CUST_ACCT_ID = #{req.businessId}
and mrc.MKT_RES_NAME = #{req.couponName}
and ciur.USE_ORDER_ID = #{req.orderNo}
and ci.COUPON_INST_NBR = #{req.couponNo}
and mrc.COUPON_TYPE = #{req.couponType}
