mapper.select:
Select ,mrc.mkt_res_name FROM coupon_inst t,mkt_res_coupon mrc WHERE t.mkt_res_id=mrc.mkt_res_id AND eff_date <= now() AND exp_date >= now()
AND t.cust_acct_id = #{req.custNum}
AND t.status_cd = #{req.statusCd}

SELECT * FROM coupon_inst ci
WHERE ci.mkt_res_id = #{mktResId} 
AND ci.status_cd = #{statusCd}
AND eff_date <= now() 
AND exp_date >=  now()
ORDER BY ci.coupon_inst_id ASC
LIMIT #{supplyNum}

mapper.update:
 			UPDATE coupon_inst SET 

			    update_date = #{updateDate}


			    ,coupon_inst_nbr = #{couponInstNbr}


			     ,mkt_res_id = #{mktResId}


			    ,coupon_amount = #{couponAmount}






			    ,eff_date = #{effDate}


			    ,exp_date = #{expDate}


			   ,status_cd = #{statusCd}


			    ,status_date = #{statusDate}


			    ,update_staff = #{updateStaff}


			    ,remark = #{remark}

			WHERE coupon_inst_id = #{couponInstId}

				AND cust_acct_id = #{custAcctId}

mapper.select:
Select 
FROM coupon_inst t
WHERE eff_date <= now()
AND exp_date >= now()

    AND cust_acct_id = #{custNum}


    AND status_cd = #{statusCd}

SELECT t.COUPON_INST_ID, t.COUPON_INST_NBR, t.MKT_RES_ID, t.COUPON_AMOUNT, t.CUST_ACCT_ID, t.EFF_DATE, t.EXP_DATE, t.STATUS_CD , mrc.MKT_RES_NAME, mrc.DISCOUNT_TYPE, mrc.COUPON_TYPE , cdr.DISCOUNT_RULE_ID, cdr.DISCOUNT_VALUE, cdr.MAX_VALUE, cdr.MIN_VALUE, cdr.RULE_AMOUNT, cdr.USE_TYPE, cdr.REUSE_FLAG, cdr.MIX_USE_FLAG, cdr.RECYCLE_FLAG, cdr.DISCOUNT_RULE_DESC FROM coupon_inst t LEFT JOIN mkt_res_coupon mrc ON t.MKT_RES_ID = mrc.MKT_RES_ID LEFT JOIN coupon_discount_rule cdr ON mrc.MKT_RES_ID = cdr.MKT_RES_ID WHERE t.eff_date <= now() AND t.exp_date >= now()
AND t.cust_acct_id = #{custNum}
AND t.status_cd = #{statusCd}
