   and a.task_status = #{req.taskStatus,jdbcType=VARCHAR}







    and a.create_user_id = #{req.createUserId,jdbcType=VARCHAR}




    and (
        b.handler_user_id = #{req.handlerUserId,jdbcType=VARCHAR}
